<?php

namespace AppBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * TermOfUseRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TermOfUseRepository extends EntityRepository
{
    
    public function getCurrentTerm($key, $lang) {
        $qb = $this->createQueryBuilder('t')
                ->select('t')
                ->where('t.langCode = :lang')
                ->setParameter('lang', $lang)
                ->andWhere('t.keyCode = :key')
                ->setParameter('key', $key)
                ->orderBy('t.id', 'ASC')
                ->setMaxResults(1)
                ->getQuery();
        $result = $qb->getResult();
        if(count($result) === 1) {
            return $result[0];
        } else {
            return null;
        }
    }
    
    public function getCurrentTerms($lang) {
        $qb = $this->createQueryBuilder('t')
                ->select('t.keyCode')
                ->distinct()
                ->orderBy('t.weight', 'ASC')
                ->getQuery();
        $keys = $qb->getArrayResult();
        $terms = array();
        foreach($keys as $key) {
            $term = $this->getCurrentTerm($key['keyCode'], $lang);
            if($term === null) {
                $term = $this->getCurrentTerm($key, 'en-US');
            }
            $terms[] = $term;
        }
        return $terms;
    }
    
}
